mapper.update:
update t_message set
	status= #{status}
where id=#{id}

mapper.select:
select id as "id", userId as "userId", friendId as "friendId", content as "content", type as "type", contentType as "contentType", status as "status", addTime as "addTime" from t_message
userId=#{userId} and friendId=#{friendId} and status=#{status}
order by addTime asc
